propertiesClass.defaultValue:
fromEvent(window, 'resize').pipe(
    map(_ => {
      return (
        document.documentElement.clientWidth <= this.MD_BOOTSTRAP_BREAKPOINT_READONLY_CLASS_PROPERTY
      );
    }),
    debounceTime(200),
    startWith(
      document.documentElement.clientWidth <= this.MD_BOOTSTRAP_BREAKPOINT_READONLY_CLASS_PROPERTY
    )
  )
