defaultValue:
(route: Partial<Route>) =>
  makeEnvironmentProviders([
    hookRoute({
      ...{
        context: ViewContext.Device,
        path: 'control',
        loadComponent: () =>
          import('./operations-list.component').then(m => m.OperationsListComponent),
        label: gettext('Control'),
        icon: 'c8y-icon c8y-icon-device-control',
        priority: 100
      },
      ...omitBy(route, isNil)
    })
  ])
