defaultValue:
(): EnvironmentProviders =>
  importProvidersFrom(
    FormlyModule.forChild({
      types: [
        {
          name: 'checkbox',
          component: FieldCheckbox
        },
        {
          name: 'boolean',
          extends: 'checkbox'
        },
        {
          name: 'switch',
          extends: 'checkbox',
          defaultOptions: {
            templateOptions: {
              switchMode: false,
              indeterminate: false
            }
          }
        }
      ]
    })
  )
