defaultValue:
(): EnvironmentProviders =>
  importProvidersFrom(
    FormlyModule.forChild({
      types: [
        {
          name: 'input',
          component: FieldInput,
          wrappers: ['c8y-form-field']
        },
        { name: 'string', extends: 'input' },
        { name: 'typeahead', extends: 'input' },
        {
          name: 'number',
          extends: 'input',
          defaultOptions: {
            templateOptions: {
              type: 'number'
            }
          }
        },
        {
          name: 'integer',
          extends: 'input',
          defaultOptions: {
            templateOptions: {
              type: 'number'
            }
          }
        }
      ]
    })
  )
